import React from "react";
import "./App.css";
import User from "./components/User";
import "tachyons";
import SearchBox from "./components/Searchbox";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      users: { robots: User, SearchBox: "" },
    };
    // this.handleClick = this.handleClick.bind(this);
  }
  componentDidMount() {
    fetch("https://jsonplaceholder.typicode.com/users")
      .then((res) => res.json())
      .then((data) => {
        this.setState({ users: data });
      })
      .catch((e) => {
        console.log(e);
      });
  }

  InputValue = (e) => {
    const value = e.target.value;
    console.log(value);
  };

  searchChange = (e) => {
    const filter = this.state.users.filter((users) => {
      return users.name
        .toLowerCase()
        .includes(this.state.SearchBox.toLowerCase());
    });
    console.log(filter);
  };
  render() {
    const { users } = this.state;
    console.log(users);

    return (
      <div className="App">
        <header className="App-header">
          <input type="text" onChange={this.searchChange} />
          <div>
            {users.map((item, i) => {
              return <User user={item} key={i} />;
            })}
          </div>
        </header>
      </div>
    );
  }
}
export default App;
